var records = {!GETRECORDIDS( $ObjectType.Contact )};

if(records.length > 0 ){
if(currentlocation_re.indexOf('apex') > -1){
if(window.parent != null ){
if(window.parent.records !=undefined){
window.parent.records=records;
} 
}
} else {
if(records.length <= 50){
if(window.parent != null ) {
if(window.parent.records !=undefined){
window.parent.records=records;
} else
window.location = "/apex/timbasurveys__collectionhub?cIds={!URLENCODE(Contact.Id)}&retURL=" + encodeURIComponent(window.location);
}
} else {
alert('Only 50 invitations can be sent from standard Contact list');
}
}
}
